#pragma once
#include "comb_testbench.h"
#include "Valu.h" // Generated by Verilator from alu.sv

class AluTestbench : public CombTestbench<Valu>{
public:
    // helper to run one ALU operation
    // no clock obvs for alu
    void evalALU(uint32_t op, uint32_t a, uint32_t b){
        top_->alu_op = op;
        top_->inA = a;
        top_->inB = b;
        
        top_->eval();
        
        tfp_->dump(context_->time());
        context_->timeInc(1);
    }
};